#!/usr/bin/env python# -*- coding: utf-8 -*-# @Time    : 2018/5/27 上午9:12# @Author  : ziyi# @File    : btcPrice.py# @Software: PyCharmfrom selenium import webdriverimport timeimport datetimeimport China_time as adef get_price1():    url1 = 'https://www.btctrade.com/btc/'    deadline_time = time.strftime("截止%d日%H点%M分", time.localtime(time.time()))    driver = webdriver.Chrome(executable_path=r'/Users/lvzeqin/Applications/chromedriver')    driver.get(url1)    time.sleep(3)    price = driver.find_element_by_xpath('//*[@id="rate-btc"]').text    # print(price)    driver.close()    btc_price = deadline_time + '，' + '比特币价格为：${}'.format(price)    return btc_pricedef get_price2():    url2 = 'https://www.btc123.com/'    deadline_time = time.strftime("截止%d日%H点%M分", time.localtime(time.time()))    driver = webdriver.Chrome(executable_path=r'/Users/lvzeqin/Applications/chromedriver')    driver.get(url2)    time.sleep(2)    price = driver.find_element_by_xpath('//*[@id="huobibtcusdt"]/td[3]/a[3]').text    driver.close()    btc_price = deadline_time + '，' + '比特币价格为：{}'.format(price)    print(btc_price)    return btc_priceif __name__ == '__main__':    btc_price = get_price2()    # today, week_day, China_time = get_time()    # haxi_head = '【哈希case】' + '\n' + today + '，' + week_day + '，' + China_time + '，' + btc_price    # print(haxi_head)    # return haxi_head