#!/usr/bin/env python# -*- coding: utf-8 -*-# @Time    : 2018/8/7 下午9:17# @Author  : ziyi# @File    : find_check.py# @Software: PyCharmdef get_contents1(path):    # 取消替换空格    send_contents = None    try:        with open(path, encoding='utf16') as f:            contents = f.readlines()        send_contents = ''.join(content for content in contents)    except Exception as e:        pass    try:        with open(path, encoding='utf8') as f:            contents = f.readlines()        send_contents = ''.join(content for content in contents)    except Exception as e:        print('不是utf8的编码格式')    return send_contentsif __name__ == '__main__':    path = "../data/count/member_count.txt"    member_infor = get_contents1(path)    # print(member_infor)    if '阿里2019届秋招群':        print('yes')