#!/usr/bin/env python# -*- coding: utf-8 -*-# @Time    : 2018/5/27 上午10:06# @Author  : ziyi# @File    : haxi_head.py# @Software: PyCharmfrom selenium import webdriverimport timeimport datetimeimport China_time as adef get_price2():    url2 = 'https://www.btc123.com/'    deadline_time = time.strftime("截止%d日%H点%M分", time.localtime(time.time()))    driver = webdriver.Chrome(executable_path=r'/Users/lvzeqin/Applications/chromedriver')    driver.get(url2)    time.sleep(2)    price = driver.find_element_by_xpath('//*[@id="huobibtcusdt"]/td[3]/a[3]').text    driver.close()    btc_price = deadline_time + '，' + '比特币价格为：{}。'.format(price)    # print(btc_price)    return btc_pricedef get_time():    d = datetime.datetime.now()    today = time.strftime('今天是%Y年%m月%d日', time.localtime(time.time()))    week = d.weekday()    if week == 0:        week = '一'    elif week == 1:        week = '二'    elif week == 2:        week = '三'    elif week == 3:        week = '四'    elif week == 4:        week = '五'    elif week == 5:        week = '六'    elif week == 6:        week = '日'    week_day = '星期'+week    # print(today, '星期'+week)    China_time = a.test()    # print(China_time)    return today, week_day, China_timedef head_content():    # url = 'https://www.btctrade.com/btc/'    btc_price = get_price2()    today, week_day, China_time = get_time()    haxi_head_content = '【哈希case】' + '\n' + today + '，' + week_day + '，' + China_time + '\n' + btc_price + '\n'    print(haxi_head_content)    return haxi_head_contentif __name__ == '__main__':    head_content()